                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         package annotation;

import org.springframework.context.ApplicationContext;
import org.springframework.context.annotation.AnnotationConfigApplicationContext;
/**
 *
 * @author Yasin_Pathan
 */
public class AnnotationMain {

    public static void main(String[] args) {
        
        ApplicationContext context = new AnnotationConfigApplicationContext(AnnotationConfig.class);
//        AnnotationAdder adder1 = (AnnotationAdder) context.getBean(AnnotationAdder.class);
        AnnotationAdder adder1 = (AnnotationAdder) context.getBean("adderBean");
        
        System.out.println("Addition is "+adder1.addNum());
        
        adder1.setNum1(50);
        adder1.setNum2(50);
        
        System.out.println("Addition is "+adder1.addNum());

        AnnotationAdder adder2 = (AnnotationAdder) context.getBean(AnnotationAdder.class);
        System.out.println("Addition is "+adder2.addNum());
        
    }

}
